import React, { useState } from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import "./App.css";
import Home from "./pages/Home/Home";
import Login from "./pages/Login/Login";
import Header from "./components/Header/Header";
import Sidebar from "./components/Sidebar/Sidebar";
import PromptInput from "./components/PromptInput/PromptInput";
import ChatWindow from "./components/ChatWindow/Chatwindow";

function App() {
  const [activeMode, setActiveMode] = useState("text");
  const [history, setHistory] = useState([]);
  const [activeItem, setActiveItem] = useState("");
  const [messages, setMessages] = useState([]);

  const handleSendPrompt = (prompt) => {
    if (prompt.trim()) {
      setMessages([...messages, { sender: "user", text: prompt }]);
      console.log("User prompt:", prompt);
    }
  };

  return (
    <Router>
      <div className="app-container">
        <Sidebar history={history} setActiveItem={setActiveItem} />
        <div className="main-content">
          <Header setActiveMode={setActiveMode} />
          <Routes>
            <Route path="/" element={<Home activeMode={activeMode} />} />
            <Route path="/login" element={<Login />} />
          </Routes>
          <PromptInput onSend={handleSendPrompt} />
        </div>
      </div>
    </Router>
  );
}

export default App;
