import React, { useState } from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import "./App.css";
import Home from "./pages/Home/Home";
import Login from "./pages/Login/Login";
import Header from "./components/Header/Header";
import Sidebar from "./components/Sidebar/Sidebar";
import PromptInput from "./components/PromptInput/PromptInput";
import ChatWindow from "./components/ChatWindow/ChatWindow"; 

function App() {
  const [activeMode, setActiveMode] = useState("text");
  const [history, setHistory] = useState([]);
  const [activeItem, setActiveItem] = useState("");
  const [messages, setMessages] = useState([]);

  const handleSendPrompt = (prompt) => {
    if (prompt.trim()) {
      const newMessage = { sender: "user", text: prompt };

      setMessages((prevMessages) => [...prevMessages, newMessage]);
      setHistory((prevHistory) => [prompt, ...prevHistory]); // Store in history
      console.log("User prompt:", prompt);

      // Simulating an AI response (replace with API call later)
      setTimeout(() => {
        const aiResponse = { sender: "ai", text: "This is an AI-generated response!" };
        setMessages((prevMessages) => [...prevMessages, aiResponse]);
      }, 1000);
    }
  };

  return (
    <Router>
      <div className="app-container">
        <Sidebar history={history} setActiveItem={setActiveItem} />
        <div className="main-content">
          <Header setActiveMode={setActiveMode} />
          <Routes>
            <Route path="/" element={<Home activeMode={activeMode} />} />
            <Route path="/login" element={<Login />} />
          </Routes>
          <ChatWindow messages={messages} /> {/* Display chat messages */}
          <PromptInput onSend={handleSendPrompt} />
        </div>
      </div>
    </Router>
  );
}

export default App;
